sp_widget.client_script:
function($rootScope,$scope,spUtil) {
	/* widget controller */
	var c = this;

	// event receiver for updateNote
	$rootScope.$on('updateNoteEvent', function(event,data) {
		console.log('updateNoteEvent received');
		console.log(data);
		console.log($scope.data.notes);
		try {
			var noteIndex = $scope.data.notes.findIndex(function(item) {
				console.log('finding');
				console.log(item, data);
				return item.sys_id === data.noteID;
			});
			console.log(noteIndex);

			// update the values
			console.log($scope.data.notes[noteIndex]);
			$scope.data.notes[noteIndex].title = data.title;
			$scope.data.notes[noteIndex].note = data.note;
			spUtil.addInfoMessage('Date Successfully updated');
			//const 
			//Object.keys()
			//var x = $scope.data.notes.findIndex(function(item){
				//return item.noteID === data.id
			//});
			//console.log(x);
		} catch(e) {
			console.log('exception caught in find function');
			console.log(e);
		}
	});

	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
		console.log('selectNoteEvent emitted');
	}


}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()){
		var noteObj = {};
		// user service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		// get the first 20 characters of the description.
		noteObj.note = noteGR.getValue('note').slice(0,20);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
})();
